        <tbody>
        <?php
        $stmt = $pdo->query("
            SELECT interventions.id, interventions.titre, interventions.description, interventions.date AS date_intervention,
           interventions.type_intervention, interventions.statut,
           clients.nom_entreprise,
           techniciens.nom AS nom_technicien, techniciens.prenom AS prenom_technicien
    FROM interventions
    JOIN clients ON interventions.client_id = clients.id
    JOIN techniciens ON interventions.technicien_id = techniciens.id ");

        foreach ($stmt as $row) {
        echo "<tr>
            <td>{$row['nom_entreprise']}</td>
            <td>{$row['nom_technicien']} {$row['prenom_technicien']}</td>
            <td>{$row['date_intervention']}</td>
            <td>{$row['type_intervention']}</td>
            <td><span class='statut {$row['statut']}'>{$row['statut']}</span></td>
            <td>
                <a href='edit.php?id={$row['id']}'>✏️</a>
                <a href='delete.php?id={$row['id']}' onclick='return confirm(\"Confirmer la suppression ?\")'>🗑️</a>
            </td>
          </tr>";
}
        ?>
